// Updated EveryPartyComponent.vue
<template>
  <div class="container">
    <h1 :class="title">Choose Your Political Party for More Information</h1>
    <div class="intro">
      <h2 class="text">Welcome! On this page, you can easily learn more about the
        different political parties in our country. Whether you're
        interested in their policies, vision, or election programs,
        we've gathered everything for you.</h2>
      <h2 class="text">
        Already have a party in mind? Discover everything about
        their policies and plans by simply clicking on the party
        of your choice!
      </h2>
    </div>
    <div class="party-container">
      <div v-for="party in displayedParties" :key="party.id" class="party-box">
        <img class="image" :src="party.image" :alt="party.name"/>
        <div class="button" @click="redirectToPartyDetail(party)">More Info</div>
      </div>
    </div>
    <div class="navigation">
      <div @click="previousPage" class="button" :class="{ disabled: currentPage === 0 }">
        Previous page
      </div>
      <div @click="nextPage" class="button"
           :class="{ disabled: (currentPage + 1) * partiesPerPage >= parties.length }">
        Next page
      </div>
    </div>
  </div>
</template>

<script>
import PartyAdapter from '../adapters/PartyAdapter.js';

export default {
  name: "AllPartiesComponent",
  data() {
    return {
      title: 'title',
      currentPage: 0,
      partiesPerPage: 9,
      parties: [],
      url: "http://localhost:8084",
    };
  },
  computed: {
    displayedParties() {
      const start = this.currentPage * this.partiesPerPage;
      const end = start + this.partiesPerPage;
      return this.parties.slice(start, end);
    },
  },
  methods: {
    redirectToPartyDetail(party) {
      this.$router.push({
        name: 'PartyDetail',
        params: { partyId: party.id },
        query: { description: party.description, importantPoints: JSON.stringify(party.importantPoints) },
      });
    },
    nextPage() {
      if ((this.currentPage + 1) * this.partiesPerPage < this.parties.length) {
        this.currentPage++;
      }
    },
    previousPage() {
      if (this.currentPage > 0) {
        this.currentPage--;
      }
    },
  },
  async mounted() {
    const adapter = new PartyAdapter(this.url);
    try {
      this.parties = await adapter.getParties();
    } catch (error) {
      console.error("Error loading party data:", error);
    }
  },
};
</script>
<style scoped>

.container {
  display: flex;
  align-items: center;
  flex-direction: column;
  margin: 10% auto;
  width: 50%;
  height: 100%;

}

.intro {
  display: flex;
  flex-direction: column;
  color: #17003C;
  width: 80%;
}

.title {
  color: #17003C;
}

.text {
  display: flex;
  margin-top: 1%;
  color: #17003C;
  font-size: 2vh;
}

.party-container {
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  justify-content: center;
}

.party-box {
  display: flex;
  flex-wrap: wrap;
  flex-direction: column;
  align-items: center;
  border-radius: 8%;
  height: 26vh;
  width: 20vh;
  margin: 3%;
  background-color: #17003C;
  color: white;


}

.button {
  display: flex;
  justify-content: center;
  align-items: center;
  color: white;
  background-color: #FF9900;
  border: 0.3vh #9F6000 solid;
  border-radius: 25px;
  height: 12%;
  width: 70%;
  font-size: 1.5vh;
  font-weight: 500;
  text-transform: uppercase;
  transition: background-color 0.3s ease, border-color 0.3s ease;
  cursor: pointer;
}

.button:hover {
  background-color: #9F6000;
  border: 0.3vh #FF9900 solid;
}

.image {
  height: 7vh;
  width: 100%;
  margin-top: 30%;
  margin-bottom: 30%;
  object-fit: contain;
}

.modal-image {
  display: flex;
  height: 30%;

}

.modal-button {
  display: flex;
  justify-content: center;
  align-items: center;
  color: white;
  background-color: #FF9900;
  border: 0.3vh #9F6000 solid;
  border-radius: 25px;
  height: 10vh;
  width: 30%;
  font-size: 1.5vh;
  font-weight: 500;
  text-transform: uppercase;
  transition: background-color 0.3s ease, border-color 0.3s ease;
  cursor: pointer;
}

.modal-button:hover {
  background-color: #9F6000;
  border: 0.3vh #FF9900 solid;

}

.modal-text {
  margin-right: 30%;
  width: 70%;
  font-size: 1vw;
}

.modal {
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 105vh;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  justify-content: center;
  align-items: center;
}

.modal-content {
  display: flex;
  left: 0;
  align-items: center;
  flex-direction: column;
  background-color: #d5d5d5;
  width: 40%;
  height: 80%;
  padding: 20px;
  border-radius: 5px;
  font-size: 1.5vh;
  transition: background-color 0.3s ease, border-color 0.3s ease;
}

.navigation {
  display: flex;
  flex-direction: row;
  justify-content: center;
  width: 20vw;
  height: 30vh;
}

* {
  font-family: "Outfit", sans-serif;
  font-optical-sizing: auto;
  font-style: normal;
}
</style>
