import {reactive} from "vue";

let isLoggedIn;

if (localStorage.getItem('token')) {
    isLoggedIn = true;
} else {
    isLoggedIn = false;
}

// 'isLoggedIn' is a property of an object.
// 'reactive' makes the property 'alive'.
export const authState = reactive({
    isLoggedIn: isLoggedIn
})
