import { authState } from "@/authState";
export default {
  name: "App",
  data() {
    return {
      isMenuOpen: false // Controls hamburger menu visibility
    };
  },
  computed: {
    isLoggedIn() {
      return authState.isLoggedIn;
    }
  },
  methods: {
    toggleMenu() {
