import { createStore } from 'vuex';

export default createStore({
    state: {
        userId: null,
        joinedCommunityId: null, // Track the community the user has joined
    },
    mutations: {
        setUserId(state, userId) {
            state.userId = userId;
        },
        setJoinedCommunity(state, communityId) {
            state.joinedCommunityId = communityId;
        },
        leaveCommunity(state) {
            state.joinedCommunityId = null; // Reset community membership
        },
    },
    actions: {
        setUserId({ commit }, userId) {
            localStorage.setItem('userId', userId);
            commit('setUserId', userId);
        },
        joinCommunity({ commit }, communityId) {
            localStorage.setItem('joinedCommunityId', communityId);
            commit('setJoinedCommunity', communityId);
        },
        leaveCommunity({ commit }) {
            localStorage.removeItem('joinedCommunityId');
            commit('leaveCommunity');
        },
        initializeStore({ commit }) {
            const userId = localStorage.getItem('userId');
            const joinedCommunityId = localStorage.getItem('joinedCommunityId');
            if (userId) commit('setUserId', Number(userId));
            if (joinedCommunityId) commit('setJoinedCommunity', Number(joinedCommunityId));
        }
    },
    getters: {
        userId: (state) => state.userId,
        joinedCommunityId: (state) => state.joinedCommunityId,
    },
});
